// import logo from './logo.svg';
import './App.css';
import { AppBar, Button, Grid, Typography } from '@material-ui/core';
import Navbar from './components/Navbar';
import MyCard from './components/MyCard';
import {getMatches} from "./api/Api";
import {Fragment, useEffect, useState} from 'react';


var request = require('request');

function updateData(postData){
  // var clientServerOptions = {
  //     uri: 'http://localhost:5000',
  //     // body: JSON.stringify(postData),
  //     // host: 'localhost',
  //     // path: '/',
  //     // port: '5000',
  //     body: postData,
  //     method: 'POST',
  //     // mode: 'no-cors',
  //     headers: {
  //         'Content-Type': 'application/json'
  //     }
  // }
  // request(clientServerOptions, function (error, response) {
  //     // console.log(error,response.body);
  //     console.log("Here at first\n");
  //     // if (error) throw error;
  //     // console.log("The response from the server was" + response.body);
  //     // console.log("Made it here");
  //     return;
  // });

  fetch ('http://localhost:5000', {method: 'POST', body: postData, mode: 'no-cors'})
  .then((res) => {
    // res.json();
    console.log(res);
  })
  .catch(err => console.log(err));
  // .then(console.log(res));
}

function getData (request) {
  fetch ('http://localhost:5000')
  .then((res) => console.log(res))
  .catch(err => console.log(err));
}

function App() { 
 const [matches, setMatches] = useState([]);
  useEffect(() => {
    getMatches()
    .then((data)=> {
      setMatches(data.matches)
      console.log("This is the data \n", typeof(data), "\n", data);
      updateData(data);
    })
    .catch(error=> {
      console.log("The error is: ", error);
      alert("Unable to load")
    });
  },[]);

  return (
    <div className="App">
      <Navbar></Navbar>
      <Typography variant="h3" color="primary" style={{marginTop:20}}>
        LIVE CRICKET SCORE
      </Typography>
      <Grid container>
        <Grid sm="2"></Grid>
        <Grid sm="8">
         {matches.map((match) =>(
           <Fragment>
             {match.type === "Twenty20"?(
                <MyCard key={match.unique_id} match ={match} />
             ) : (
               ""
             )}
           </Fragment>
          ))}
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
